
var mongo = require( 'mongodb' );

module.exports =
    function ObjectID( id ) {
        var _id;
        if ( !id ) return null;
        try {
            _id = new mongo.ObjectID( String( id ));
        } catch( err ) {
            console.log( 'Error ObjectID:', err, id );
        }
        return _id || null;
    };